code-samples.code:
curl --location --request POST 'https://api.conekta.io/plans' \
--header 'Accept: application/vnd.conekta-v2.0.0+json' \
--header 'Content-Type: application/json' \
-u key_xxxxxxxxxxxxxx: \
--data-raw '{
// Id del Plan que usarás, REFERENCIA (ID INTERNO)
       "id": "Plan_API_TEST",     		  	
// Nombre de tu plan, DETALLE DEL PLAN
       "name": "Plan de elementos API", 
// MONTO del plan, dependerá de la frecuencia y recurrencia
       "amount": 77700,  	
// Sólo soporta MXN, MONEDA
       "currency": "MXN",  
// Weekly/biweekly/montly/yearly | semanal/quincenal/mensual/anual
       "interval": "month",  
// Frecuencia del cargo
       "frequency": 1,
// Colocar número de días o de lo contrario 0
       "trial_period_days": 30,      
// Número de repeticiones de la frecuencia NÚMERO DE CARGOS QUE SE HARÁN
       "expiry_count":12			          	
}
'

